import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { searchIcon } from "../functions/icons";

const PokeDetailsPage = () => {
  const { id } = useParams();
  const [pokemon, setPokemon] = useState([]);

  useEffect(() => {
    getPokemonOnID();
  }, []);

  const getPokemonOnID = async () => {
    try {
      const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}/`);
      const data = await res.json();
      setPokemon(data);
    } catch (error) {
      console.log(error);
    }
  };

  console.log(pokemon.types[0].type.name);

  if (pokemon.id) {
    return (
      <div className="detail-section">
        <div
          className="detail-header"
          style={{
            background: `linear-gradient(0deg, #fff 20%, var(--bg-poke-color-dark-${pokemon.types[0].type.name}) 20%)`,
          }}
        >
          {pokemon.id && (
            <div className="detail-header-card">
              <img
                className="card__image"
                width={300}
                height={300}
                src={pokemon.sprites["front_default"]}
                alt=""
                loading="lazy"
              />
              <h2>
                {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
              </h2>
            </div>
          )}
        </div>
        <button onClick={getPokemonOnID}>Pokemon</button>
      </div>
    );
  }
};

export default PokeDetailsPage;
