import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { searchIcon } from "../functions/icons";

const PokeDetailsPage = ({ pokemon, getPokemonOnID }) => {
  const { id } = useParams();
  // const [pokemon, setPokemon] = useState([]);

  useEffect(() => {
    getPokemonOnID(id);
  }, []);

  return (
    <div className="detail-section">
      <div className="detail-header">
        <div
          className="detail-header-card"
          style={{
            background: `var(--bg-poke-color-light-${pokemon.types[0].type.name})`,
          }}
        >
          <img
            className="card__image"
            width={300}
            height={300}
            src={pokemon.sprites["front_default"]}
            alt=""
            loading="lazy"
          />
          <h2>
            {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
          </h2>
        </div>
      </div>
    </div>
  );
};

export default PokeDetailsPage;

