import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../colors.css";

const PokeListPage = () => {
  const [allPokemons, setAllPokemons] = useState([]);
  const [loadPoke, setLoadPoke] = useState(
    "https://pokeapi.co/api/v2/pokemon?limit=20&offset=0"
  );
  const navigate = useNavigate();

  useEffect(() => {
    // getAllPokemons();
    // console.log(allPokemons);
  }, []);

  const getAllPokemons = async () => {
    const res = await fetch(loadPoke);
    const data = await res.json();
    setLoadPoke(data.next);

    function createPokemonObject(result) {
      result.forEach(async (pokemon) => {
        const res = await fetch(
          `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
        );
        const data = await res.json();
        setAllPokemons((currentList) => [...currentList, data]);
      });
    }
    createPokemonObject(data.results);
  };

  return (
    <div>
      <div className="grid-container">
        {allPokemons.map((pokemon, key) => {
          return (
            <div
              key={key}
              className="pokemon-card"
            >
              <div
                className="top-container"
                style={{
                  background: `var(--bg-poke-color-dark-${pokemon.types[0].type.name})`,
                }}
              >
                <div className="banner-content">
                  <img
                    src="pokeball.png"
                    height={"20px"}
                    alt=""
                  />
                  <p>{pokemon.types[0].type.name.toUpperCase()}</p>
                </div>
              </div>

              <div
                className="intro-container"
                style={{
                  background: `var(--bg-poke-color-light-${pokemon.types[0].type.name})`,
                }}
              >
                <img
                  className="card__image"
                  width={120}
                  height={120}
                  src={pokemon.sprites["front_default"]}
                  alt=""
                  loading="lazy"
                />
                <h2>
                  {pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}
                </h2>
              </div>

              <div className="stats-container">
                {pokemon.stats.map((poke) => {
                  return (
                    <div className="stat-row">
                      <p>{poke.stat.name}</p> <p>{poke.base_stat}</p>
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>

      <button onClick={() => getAllPokemons()}>More Pokemons</button>
    </div>
  );
};

export default PokeListPage;
