import React, { useEffect, useState } from "react";
import "../colors.css";
import GridLayout from "../layout/GridLayout";
import ListLayout from "../layout/ListLayout";
import TopBarContainer from "./TopBarContainer";

const PokeListPage = () => {
  const [allPokemons, setAllPokemons] = useState([]);
  const [pokeUrl, setPokeUrl] = useState(
    "https://pokeapi.co/api/v2/pokemon?limit=20&offset=0"
  );
  const [contentFormToggle, setContentFormToggle] = useState(false);

  useEffect(() => {
    getAllPokemons();
    console.log(allPokemons);
  }, []);

  const getAllPokemons = async () => {
    const res = await fetch(pokeUrl);
    const data = await res.json();
    setPokeUrl(data.next);

    const createPokemonObject = async (result) => {
      const newPokemons = await Promise.all(
        result.map(async (pokemon) => {
          const res = await fetch(
            `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
          );
          return res.json();
        })
      );
      setAllPokemons((currentList) => [...currentList, ...newPokemons]);
    };
    createPokemonObject(data.results);

    // function createPokemonObject(result) {
    //   result.forEach(async (pokemon) => {
    //     const res = await fetch(
    //       `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
    //     );
    //     const data = await res.json();
    //     setAllPokemons((currentList) => [...currentList, data]);
    //   });
    // }
  };

  return (
    <div className="main-content">
      <TopBarContainer
        contentFormToggle={contentFormToggle}
        setContentFormToggle={setContentFormToggle}
      />

      {contentFormToggle ? (
        <GridLayout
          allPokemons={allPokemons}
          getAllPokemons={getAllPokemons}
        />
      ) : (
        <ListLayout
          allPokemons={allPokemons}
          getAllPokemons={getAllPokemons}
        />
      )}
    </div>
  );
};

export default PokeListPage;
