import React from "react";

const ListLayout = ({ allPokemons, getAllPokemons }) => {
  return (
    <div className="pokemon-list">
      <div className="list-container">
        {allPokemons.map((pokemon, key) => {
          return (
            <div className="poke-row">
              <div
                className="wor-top-container"
                style={{
                  background: `var(--bg-poke-color-dark-${pokemon.types[0].type.name})`,
                }}
              >
                <div className="name-holder">
                  <img
                    src="pokeball.png"
                    height={"20px"}
                    alt=""
                    id="poke-ball"
                  />
                  <p id="row-name">
                    {pokemon.name.charAt(0).toUpperCase() +
                      pokemon.name.slice(1)}
                  </p>
                </div>

                <div className="type-holder">
                  <p>{}</p>
                </div>
              </div>

              <div className="poke-row-content">
                <div
                  className="row-img-container"
                  style={{
                    background: `var(--bg-poke-color-light-${pokemon.types[0].type.name})`,
                  }}
                >
                  <img
                    className="card__image"
                    width={120}
                    height={120}
                    src={pokemon.sprites["front_default"]}
                    alt=""
                    loading="lazy"
                  />
                </div>
                <div className="row-stats-container">
                  {pokemon.stats.map((poke) => {
                    return (
                      <div className="stat-values">
                        <p>{poke.stat.name}</p>
                        <h2>{poke.base_stat}</h2>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ListLayout;
