import React, { useEffect, useState } from "react";
import "../colors.css";
import GridLayout from "../layout/GridLayout";
import ListLayout from "../layout/ListLayout";
import TopBarContainer from "./TopBarContainer";

const PokeListPage = () => {
  const [allPokemons, setAllPokemons] = useState([]);
  const [pokeUrl, setPokeUrl] = useState("https://pokeapi.co/api/v2/pokemon");
  const [layoutToggle, setLayoutToggle] = useState(false);
  const [nextUrl, setNextUrl] = useState("");

  useEffect(() => {
    getAllPokemons();
  }, []);

  // const getAllPokemons = async () => {
  //   const res = await fetch(pokeUrl);
  //   const data = await res.json();

  //   // console.log(res);
  //   // console.log(data);

  //   function createPokemonObject(result) {
  //     result.forEach(async (pokemon) => {
  //       const res = await fetch(
  //         `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
  //       );
  //       const data = await res.json();
  //       if (allPokemons.length === 0) {
  //         setAllPokemons((currentList) => [...currentList, data]);
  //       } else {
  //         setAllPokemons((currentList) => [...currentList, data]);
  //       }
  //     });
  //   }
  //   createPokemonObject(data.results);
  //   setPokeUrl(data.next);
  // };

  const getPokemonHandler = async () => {
    const res = await fetch(pokeUrl);
    const data = await res.json();
    setNextUrl(data.next);
    getAllPokemons(data.results);

    console.log(data.results);
  };

  const getAllPokemons = async (res) => {
    res.map(async (item) => {
      const result = await fetch(item.url);
      const data = await result.json();
      setAllPokemons((currentList) => {
        currentList = [...currentList, data];
        return currentList;
      });
      console.log(data);
    });
  };

  return (
    <div className="main-content">
      <TopBarContainer
        lyoutToggle={layoutToggle}
        setLayoutToggle={setLayoutToggle}
      />

      {layoutToggle ? (
        <GridLayout
          allPokemons={allPokemons}
          getAllPokemons={getPokemonHandler}
        />
      ) : (
        <ListLayout
          allPokemons={allPokemons}
          getAllPokemons={getPokemonHandler}
        />
      )}
    </div>
  );
};

export default PokeListPage;
