import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { searchIcon } from "../functions/icons";

const PokeDetailsPage = () => {
  const { id } = useParams();
  const [IsLoading, setIsLoading] = useState(false);
  const [pokemon, setPokemon] = useState([]);

  useEffect(() => {
    getPokemonOnID();
  }, []);

  const getPokemonOnID = async () => {
    setIsLoading(true);
    const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}/`);
    const pokeData = await res.json();
    setPokemon(pokeData);
    setIsLoading(false);
  };
  return (
    <div className="main-content">
      <div className="detail-section">
        <div
          className="detail-top-container"
          style={{
            background: `var(--bg-poke-color-dark-${pokemon.types[0].type.name})`,
          }}
        >
          <div className="detail-top-container-content">{pokemon.name}</div>
        </div>
      </div>
    </div>
  );
};

export default PokeDetailsPage;

