import React from "react";

const PokeListPage = () => {
  const showPokemons = async (limit = 20, offset = 0) => {
    const api = await fetch(
      `https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=${offset}`
    );
    const data = await api.json();
    const promises = await data.results.map(async (pokemon) => {
      const result = await fetch(pokemon.url);
      const res = await result.json();
      return res;
      console.log(res);
    });

    const results = await Promise.all(promises);

    // this.setState((prev) => ({
    //   search: [],
    //   pokemons: [...prev.pokemons, ...results],
    //   notFound: false,
    //   total: prev.total + results.length,
    // }));
  };

  return (
    <div>
      <button onClick={showPokemons}>Get Pokemons!</button>
    </div>
  );
};

export default PokeListPage;
